import React,{ Component} from 'react';
import axios from 'axios';
import Comment from './Comment';
import ListComment from './ListComment'

class Blog_detail  extends Component {
  constructor(props) {
    super(props);
    this.state = {
      detail: {},
      comment: [],
      idReplay: "",
    };
    this.renderDetail = this.renderDetail.bind(this);
    this.getComment = this.getComment.bind(this);
    this.getIds = this.getIds.bind(this);
  }
  componentDidMount(){
    const getId = this.props.match.params.id;
    console.log(getId);
    axios
      .get("http://localhost/laravel/public/api/blog/detail/" + getId)
      .then((res) => {
        //console.log(res);
        this.setState({
          detail: res.data.data,
          comment: res.data.data.comment,
        });
      })
      .catch((error) => console.log(error));
  }

  renderDetail(){
      let {detail} = this.state;
      
      if(Object.keys(detail).length > 0){

          // return Object.keys(detail).map((key,index) => {
            
              return (
                <div  className="single-blog-post">
                    <h3>{detail['title']}</h3>
                    
                    <div className="post-meta">
                      <ul>
                        <li><i className="fa fa-user" /> Mac Doe</li>
                        <li><i className="fa fa-clock-o" /> 1:33 pm</li>
                        <li><i className="fa fa-calendar" /> DEC 5, 2013</li>
                      </ul>
                    </div>
                    <a href>
                        <img src={"http://localhost/laravel/public/upload/Blog/image/" + detail['image']} alt="" />
                    </a>
                    <p>{detail['description']} </p>
                    <div className="pager-area"> 
                        <ul className="pager pull-right">
                          <li><a href="#">Pre</a></li>
                          <li><a href="#">Next</a></li>
                        </ul>
                    </div>
                </div>
              )
          // })
      }
  }
  getComment(x) {
    //  console.log(x);
    this.setState({
      comment: this.state.comment.concat(x),
    });
  }
  render(){
    // console.log(this.state.comment);
    return (
      <div class="col-sm-9">
          <div class="blog-post-area">
              <h2 class="title text-center">Latest From our Blog</h2>
              {this.renderDetail()}
          </div>
          <ListComment
            getId={this.props.match.params.id}
            getComment={this.getComment}
            
        />
          <Comment
            getId={this.props.match.params.id}
            getComment={this.getComment}
        />
      </div>
      
    )
  }
}
export default Blog_detail;
