import react,{ Component} from 'react';
import axios from 'axios';
import Comment from './Comment';
import ListComment from './ListComment'
import { Link } from 'react-router-dom';

class Blog_detail  extends Component {
  constructor(props){
    super(props)
    this.state = {
        id:'',
        detail :{},
        comment :[]
    }
    
  }
  componentDidMount(){
      let getId = this.props.match.params.id;
      axios.get("http://localhost/laravel/public/api/blog/detail/" + getId)
      .then(res => {
          console.log(res)
          let detail = res.data.data;
          this.setState({ detail });
          
          let comment =res.data.data.comment;
          console.log(comment)
         
      })
      .catch(error => console.log(error));
  }

  renderDetail(){
      let detail = this.state.detail;
      
      if(Object.keys(detail).length > 0){

          // return Object.keys(detail).map((key,index) => {
            
              return (
                <div  className="single-blog-post">
                    <h3>{detail['title']}</h3>
                    
                    <div className="post-meta">
                      <ul>
                        <li><i className="fa fa-user" /> Mac Doe</li>
                        <li><i className="fa fa-clock-o" /> 1:33 pm</li>
                        <li><i className="fa fa-calendar" /> DEC 5, 2013</li>
                      </ul>
                    </div>
                    <a href>
                        <img src={"http://localhost/laravel/public/upload/Blog/image/" + detail['image']} alt="" />
                    </a>
                    <p>{detail['description']} </p>
                    <div className="pager-area"> 
                        <ul className="pager pull-right">
                          <li><a href="#">Pre</a></li>
                          <li><a href="#">Next</a></li>
                        </ul>
                    </div>
                </div>
              )
          // })
      }
  }
  getComment(x) {
      console.log(x);
      this.setState({
          comment: this.state.comment.concat(x),
      });
  }
  render(){
    return (
      <div class="col-sm-9">
          <div class="blog-post-area">
              <h2 class="title text-center">Latest From our Blog</h2>
              {this.renderDetail()}
          </div>
          <ListComment
            getId={this.props.match.params.id}
            getComment={this.getComment}
        />
          <Comment
            getId={this.props.match.params.id}
            getComment={this.getComment}
        />
      </div>
      
    )
  }
}
export default Blog_detail;
