import React, { Component } from "react";
// import axios from "axios";

class Update extends Component{
    constructor(props){
        super(props)
        this.state = {
            msg : '',
            name : '',
            email : '',
            password : '',
            address : '',
            phone : '',
            file : '',
            avatar : '',
            formError : {},
        }
        // this.handleSubmit = this.handleSubmit.bind(this)
        // this.handleValue = this.handleValue.bind(this);
        // this.handleInputFile = this.handleInputFile.bind(this);
    }

    // handleValue(e){
    //     let nameInput = e.target.name;
    //     let value = e.target.value;
    //     this.setState({
    //         nameInput : value
    //     })
    //     // console.log(value)
    // }

    // handleInputFile (e){ 
    //     const file = e.target.files;
    //     console.log(file)
    //     // send file to api server
    //     let reader = new FileReader();
    //     reader.onload = (e) => {
    //         this.setState({
    //             avatar: e.target.result, //cai nay de gui qua api
    //             file: file[0] //cai nay de toan bo thong file upload vao file de xuong check validate
    //         })
    //     };
    //     reader.readAsDataURL(file[0]);
    // }

    render() {
        return (
          <div className="col-sm-8 col-sm-offset-1">
            <div className="login-form">
              <h2>User UpDate!</h2>
              
              <form onSubmit={this.handleSubmit} enctype="multipart/form-data">
                <input
                    type="text"
                    name="name"
                    value={this.state.name}
                    placeholder="Name"
                    onChange={this.handleValue}
                />
                <input
                    type="text"
                    name="email"
                    disabled
                    value={this.state.email}
                    placeholder="Email Address"
                    onChange={this.handleValue}
                />
                <input
                    type="password"
                    name="password"
                    value={this.state.password}
                    placeholder="Email Address"
                    onChange={this.handleValue}
                />
                <input
                    type="text"
                    name="address"
                    value={this.state.address}
                    placeholder="Address"
                    onChange={this.handleValue}
                />
                <input type="text" name="phone" 
                    value={this.state.phone} 
                    placeholder="Phone Number"
                    onChange={this.handleValue}
                />
                <input type="file" name="avatar" onChange={this.handleInputFile} />
                <button
                  type="submit"
                  className="btn btn-default"
                  style={{ }}
                >
                  Update
                </button>
              </form>
            </div>
          </div>
        );
      }
}
export default Update;
