import axios from "axios";
import React, { Component } from "react";

class Update extends Component {
    constructor(props) {
      super(props);
      this.state = {
          name: "",
          email: "",
          password: "",
          address: "",
          phone: "",
          avatar: "",
          formErrors: {},
          file: "",
          msg: "",
      };

      this.handleValue = this.handleValue.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      // this.handleInputFile = this.handleInputFile.bind(this);
    }
    
  handleValue(e) {
      let nameInput = e.target.name;
      let value = e.target.value;
      this.setState({
        [nameInput]: value,
      });
      // console.log(value);
  }

  componentDidMount(){
      const getInfo = localStorage.getItem("info");
      if(getInfo){
          convInfo = JSON.parse('getInfor')
          this.setState({
            name: getInfo.Auth.name,
            email: getInfo.Auth.email,
            address: getInfo.Auth.address,
            phone: getInfo.Auth.phone,
          });
      }
      else {
          this.props.history.push("/login");
      }
  }
  handleSubmit(e){

  }
  render() {
    return (
      <div className="col-sm-8 col-sm-offset-1">
        <div className="login-form">
          <h2>User UpDate!</h2>
          {/* {this.state.msg} */}
          {/* <FormErrors formErrors={this.state.formErrors} /> */}
          <form onSubmit={this.handleSubmit} enctype="multipart/form-data">
            <input
              type="text"
              name="name"
              value={this.state.name}
              placeholder="Name"
              onChange={this.handleValue}
            />
            <input
              type="text"
              name="email"
              disabled
              value={this.state.email}
              placeholder="Email Address"
              onChange={this.handleValue}
            />
            <input
              type="text"
              name="password"
              value={this.state.password}
              placeholder="Password"
              onChange={this.handleValue}
            />
            <input
              type="text"
              name="address"
              value={this.state.address}
              placeholder="Address"
              onChange={this.handleValue}
            />
            <input
              type="text"
              name="phone"
              value={this.state.phone}
              placeholder="Phone Number"
              onChange={this.handleValue}
            />
            <input type="file" name="avatar" onChange={this.handleInputFile} />
            <button
              type="submit"
              className="btn btn-default"
              style={{ marginBottom: "20px" }}
            >
              Update
            </button>
          </form>
        </div>
      </div>
    );
  }
}
export default Update;
