import axios from 'axios';
import React,{ Component} from 'react';
import ErrorForm from './ErrorForm'

class Login  extends Component{
    constructor(props){
        super(props)
        this.states = {           
            pass: "",
            email: "",
            formError: {},
            msg:''
        }
        this.handleValue = this.handleValue.bind(this);
        this.submitForm = this.submitForm.bind(this);
    }
    //    == so sanh bthuon 1=="1"
    //    === so sanh kieu 1===1
    handleValue(e){ 
        let nameInput = e.target.name;
        let value = e.target.value;
        this.setState({
            [nameInput]: value,
        })
        // console.log(value)
    }
    submitForm(e){
        e.preventDefault();
        // let flag = true;
        // let {name,email,pass} = this.states;
        // let errorSubmit = this.state.formError;


    }
    render(){
        // console.log(this.state.formError)
        return(
            <div className="col-sm-4 col-sm-offset-1">
                <div className="login-form">
                    <h2>Login to your account</h2>
                    <form  onSubmit={this.submitForm}>    
                        <input type="text" placeholder="Email" name="email" value={this.state.value} onChange={this.handleValue} />
                        <input type="text" placeholder="Password" name="pass" value={this.state.value} onChange={this.handleValue} />
                        <button type="submit" class="btn btn-default">login</button>
                        
                    </form>
                </div>
            </div>
        )
    }  
}

export default Login;
