import React,{ Component} from 'react';
import axios from "axios";
// import StarRatings from './react-star-ratings';
// import FormErrors from "../Member/FormErrors";

class Rate  extends Component{
    constructor(props){
        super(props)
        this.state = {
            msg : '',
            rating : 0,
            FormErrors : {}
        }
        // this.StarRatings = this.StarRatings.bind(this);
    }

    componentDidMount(){
        const getId = this.props.getId;
        axios.get('http://localhost/laravel/public/api/blog/rate/' + getId)
        .then(res => {
            // console.log(res)
            let total = 0;
            let id = res.data.data;
            console.log(id);
            id.map((value , key) =>{
                // console.log(value['rate'])
                total = total + value['rate'];
            })
            let avg = total / id.length;
            this.setState({
                rating :avg
            })
        })
        .catch(error => console.log(error));
    }

    changeRating(newRating,name){
        const isLogin = localStorage.getItem('isLogin')
        if(!JSON.parse(isLogin)){
            alert('Vui long dang nhap')
        }
        else{
            this.setState({
                rating : newRating
            })
            const getId = this.props.getId;
            let url = "http://localhost/laravel/public/api/blog/rate/ "+ getId;
            const userData = JSON.parse(localStorage["info"]);
            console.log(userData.Auth.id);
        }
    }

    render(){
        return(
            <div className="rating-area">
                <ul className="ratings">
                    <li className="rate-this">Rate this item:</li>
                    <li>
                        <i className="fa fa-star color" />
                        <i className="fa fa-star color" />
                        <i className="fa fa-star color" />
                        <i className="fa fa-star" />
                        <i className="fa fa-star" />
                    </li>
                    <li className="color">(6 votes)</li>
                </ul>
                <ul className="tag">
                    <li>TAG:</li>
                    <li><a className="color" href>Pink <span>/</span></a></li>
                    <li><a className="color" href>T-Shirt <span>/</span></a></li>
                    <li><a className="color" href>Girls</a></li>
                </ul>
            </div>/*/rating-area*/
        )
    }     
}

export default Rate;
