import axios from "axios";
import React, { Component } from "react";
import ErrorForm from "../../Member/ErrorForm";

class Edit extends Component{
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            price: "",
            category: "",
            brand: "",
            categoryArr: [],
            brandArr: [],
            sale: "",
            status: 1,
            des: "",
            company: "",
            file: "",
            formError: {},
            msg: "",
            avatarCheckBox :[] //hinh anh muon xoa
        };
        this.handleValue = this.handleValue.bind(this);
        this.renderSale = this.renderSale.bind(this);
        this.renderCatelogy = this.renderCatelogy.bind(this);

    }
    componentDidMount() {
        axios
          .get("http://localhost/laravel/public/api/category-brand")
          .then((res) => {
            // console.log(res);
            this.setState({
              categoryArr: res.data.category,
              brandArr: res.data.brand,
            });
            
          });

    }

    handleValue(e) {
        let nameInput = e.target.name;
        let value = e.target.value;
        this.setState({
          [nameInput]: value,
        });
    }
    handleInputFile=(e)=>{
      let file =  e.target.files;
      this.setState({file})
    }
    renderSale(e){
        let { status } = this.state;
        if (status == 0) {
          return (
            <>
              <input  id="productSale" type="text" name="sale" placeholder="0" value={this.state.value}  onChange={this.handleValue}/>
              <span>%</span>
            </>
          );
        }
    }
    renderCatelogy(e){
        let {categoryArr} = this.state;
        if(categoryArr.length > 0){
            return categoryArr.map((value,key)=>{
                return (
                    <option value={value["id"]}> {value["catelogy"]}</option>
                )
            })
        }
    }
    renderBrand(e){
        let {brandArr}= this.state;
        if(brandArr.length > 0){
            return brandArr.map((value,key)=>{
                return(
                    <option value= {value["id"]}> {value["brand"]} </option>
                )
            })
              
            
        }
    }

   
  
    render() {
        return (
          <div className="col-sm-9">
            <div className="signup-form">
              {/*sign up form*/}
              <h2>Create Product</h2>
             
              {/* <ErrorForm formError={this.state.formError} /> */}
              <form
                action="#"
                enctype="multipart/form-data"
                onSubmit={this.addProduct}
              >
                <input
                  type="text"
                  placeholder="Name Product"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleValue}
                />
    
                <input
                  type="text"
                  placeholder="Price"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleValue}
                />
    
                <select
                  type="text"
                  name="category"
                  value={this.state.category}
                  onChange={this.handleValue}
                >
                  <option value="">Category</option>
                </select>
                {this.renderCatelogy()}
                <select
                  type="text"
                  name="brand"
                  value={this.state.brand}
                  onChange={this.handleValue}
                >
                  <option value="">Brand</option>
                  
                </select>
                {this.renderBrand()}
                <select
                  type="text"
                  name="status"
                  value={this.state.status}
                  onChange={this.handleValue}
                >
                  <option value="1">New</option>
                  <option value="0">Sale</option>
                  
                </select>
                {this.renderSale()}
                
                <input
                  type="text"
                  placeholder="Company"
                  name="company"
                  value={this.state.company}
                  onChange={this.handleValue}
                />
                <input
                  type="file"
                  name="img"
                  multiple
                  onChange={this.handleInputFile}
                />
                <textarea
                  name="des"
                  placeholder="Product Description"
                  rows={11}
                  defaultValue={""}
                  value={this.state.des}
                  onChange={this.handleValue}
                />
    
                <button type="submit" className="btn btn-default">
                  Sign up
                </button>
              </form>
            </div>
            {/*/sign up form*/}
          </div>
        );
      }
}
export default Edit;
