import axios from 'axios';
import React,{ Component} from 'react';
import ErrorForm from './ErrorForm'

class Register  extends Component{
    constructor(props){
        super(props)
        this.state = {
            Email :'',
            Pass : '',
            Name : '',
            Phone : '',
            Address : '',
            Avatar : '',
            formError: {}
        }
        this.handleValue = this.handleValue.bind(this);
        this.submitForm = this.submitForm.bind(this);

    }
    handleValue(e){ 
        let nameInput = e.target.name;
        let value = e.target.value;
        this.setState({
            [nameInput]: value,
        })
        // console.log(value)
    }
    submitForm(e){
        e.preventDefault();
        let flag = true;
        let { getName, getEmail, getPass, getPhone, getAddress } = this.state;
        let errorSubmit = this.state.formError;
       
        if(getName == ''){
            flag = false;
            errorSubmit.getName = 'Vui long nhap ten';
        }
        if(getEmail == ''){
            flag = false;
            errorSubmit.getEmail = 'Vui long nhap email';
        }
        if(getPass == ''){
            flag = false;
            errorSubmit.getPass = 'Vui long nhap password';
        }
        if(getPhone == ''){
            flag = false;
            errorSubmit.getPhone = 'Vui long nhap sdt';
        }
        if(getAddress == ''){
            flag = false;
            errorSubmit.getAddress = 'Vui long nhap dia chi';
        }
        if(!flag){
            this.setState({
                formError:errorSubmit
            })
        }
        else{
            // const data = {
            //     name : this.state.name,
            //     email : this.state.email,
            //     pass : this.state.pass,
            //     phone : this.state.phone,
            //     address : this.state.address,
            //     level : 0
            // }
            alert("abc")
          } 
        
    }

    render(){
        // console.log(this.state.formError)
        return(
            <>
                <ErrorForm formError={this.state.formError}/>
                <div class="col-sm-4">
                    <div class="signup-form">
                        <h2>New User Signup!</h2>
                        <form  onSubmit={this.submitForm}>
                            <input type="text" placeholder="Name" name="Name" value={this.state.value} onChange={this.handleValue} />
                            <input type="text" placeholder="Email" name="Email" value={this.state.value} onChange={this.handleValue} />
                            <input type="text" placeholder="Password" name="Pass" value={this.state.value} onChange={this.handleValue} />                          
                            <input type="text" placeholder="Address" name="Address" value={this.state.value} onChange={this.handleValue} />
                            <input type="text" placeholder="Phone" name="Phone" value={this.state.value} onChange={this.handleValue} /> 
                            <button type="submit" class="btn btn-default">Sign up</button>
				        </form>
                    </div>
                </div>
                
            </>
        )
    }  
}

export default Register;
