import react,{ Component} from 'react';
import ErrorForm from './ErrorForm'

class Register  extends Component{
    constructor(props){
        super(props)
        this.state = {
            email :'',
            pass : '',
            name : '',
            phone : '',
            address : '',
            avatar : '',
            level : '',
            formError: {}
        }
        this.handleValue = this.handleValue.bind(this);
        this.submitForm = this.submitForm.bind(this);

    }
    handleValue(e){ 
        let nameInput = e.target.name;
        let value = e.target.value;
        this.setState({
            [nameInput]: value,
        })
        // console.log(value)
    }
    submitForm(e){
        e.preventDefault();
        let flag = true;
        let { name, email, pass, phone, address } = this.state;
        let errorSubmit = this.state.formError;
       
        if(name == ''){
            flag = false;
            errorSubmit.name = 'Vui long nhap ten';
        }
        if(email == ''){
            flag = false;
            errorSubmit.email = 'Vui long nhap email';
        }
        if(pass == ''){
            flag = false;
            errorSubmit.pass = 'Vui long nhap password';
        }
        if(phone == ''){
            flag = false;
            errorSubmit.phone = 'Vui long nhap sdt';
        }
        if(address == ''){
            flag = false;
            errorSubmit.address = 'Vui long nhap dia chi';
        }
        if(!flag){
            this.setState({
                formError:errorSubmit
            })
        }
        else{
            alert('Dang ky thanh cong')
        }
    }

    render(){
        // console.log(this.state.formError)
        return(
            <>
                <ErrorForm formError={this.state.formError}/>
                <div class="col-sm-4">
                    <div class="signup-form">
                        <h2>New User Signup!</h2>
                        <form  onSubmit={this.submitForm}>
                        <input type="text" name="Email Address" value={this.state.value} onChange={this.handleValue} />
                        <input type="text" name="Password" value={this.state.value} onChange={this.handleValue} />
                        <input type="text" name="Name" value={this.state.value} onChange={this.handleValue} />
                        <input type="text" name="Address" value={this.state.value} onChange={this.handleValue} />
                        <input type="text" name="Phone" value={this.state.value} onChange={this.handleValue} />
                        <input   type="text"  placeholder="Name" name="name" value={this.state.value} onChange={this.handleValue}/>
                        <button type="submit" class="btn btn-default">Signup</button>
				</form>
                    </div>
                </div>
                
            </>
        )
    }  
}

export default Register;
